package programsDemo;

public class Swapnumswithoutthirdvar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//x=x+y;
		//y=x-y;
		//x=x-y;
		
		int x=10,y=20;
		System.out.println(x+"Before Swapping " +y);
		x=x+y;
		y=x-y;
		x=x-y;
		
		System.out.println(x+"After Swapping "+y);
		
	}

}
